mapper.select:
SELECT 

FROM advertisement a

WHERE a.id = #{id}

		SELECT 

		FROM advertisement a


			a.del_flag = #{DEL_FLAG_NORMAL}
			${dataScope}

                AND a.state = #{state}


                AND a.title LIKE
                '%'||#{title}||'%'
                '%'+#{title}+'%'
                concat('%',#{title},'%')


                AND a.type = #{type}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM advertisement a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE advertisement SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
