mapper.select:
SELECT 

FROM homework a

WHERE a.id = #{id}

		SELECT 

		FROM homework a


			a.del_flag = #{DEL_FLAG_NORMAL}
			${dataScope}

				AND a.name LIKE 
					'%'||#{name}||'%'
					'%'+#{name}+'%'
					concat('%',#{name},'%')


				AND a.type = #{type}


                AND a.course_level = #{courseLevel}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM homework a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE homework SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
		SELECT

		FROM homework a


			a.del_flag = #{DEL_FLAG_NORMAL}
			${dataScope}
			and not EXISTS
			(
				select 1 from sys_homework_office sho where sho.homeworkid=a.id and sho.officeid=#{office.id}
			)

				AND a.name LIKE
				'%'||#{name}||'%'
				'%'+#{name}+'%'
				concat('%',#{name},'%')


				AND a.type = #{type}


                AND a.course_level = #{courseLevel}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC
