mapper.select:
SELECT 

FROM information a

WHERE a.id = #{id}

SELECT 

FROM information a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM information a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE information SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
