mapper.select:
SELECT 

FROM material a

WHERE a.id = #{id}

SELECT 

FROM material a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.state = #{state}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM material a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE material SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
