mapper.select:
SELECT 

FROM notice a

WHERE a.id = #{id}

SELECT
FROM notice a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.state = #{state}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM notice a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE notice SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
