mapper.select:
SELECT 

FROM public_course a

WHERE a.id = #{id}

SELECT 

FROM public_course a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM public_course a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE public_course SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
