mapper.select:
SELECT 

FROM score_exchange a

WHERE a.id = #{id}

SELECT 

FROM score_exchange a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.score = #{score}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM score_exchange a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE score_exchange SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
