mapper.select:
SELECT 

FROM score_record a

WHERE a.id = #{id}

SELECT 

FROM score_record a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.user LIKE 
			'%'||#{user.id}||'%'
			'%'+#{user.id}+'%'
			concat('%',#{user.id},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM score_record a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE score_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
