mapper.select:
SELECT ifnull(a.id,'') as "user.id", ifnull(a.name,'') as "user.name", ifnull(a.english_name,'') AS "user.englishName", ifnull(a.score,'') AS "user.score", ifnull(a.phone,'') AS "user.phone", ifnull(a.mobile,'') AS "user.mobile", ifnull(a.photo,'') AS "user.photo", ifnull(so.id,'') as "office.id", ifnull(so.name,'') as "office.name", ifnull(uh.total,'0') as "homeworkFinishToal" FROM sys_user a left join sys_user_office suo on a.id=suo.userid left join sys_office so on suo.officeid=so.id left join ( select count(*) total ,suh.userid from sys_user_homework suh left join homework h on suh.homeworkid=h.id where suh.state in ('1','2')
AND h.type =#{homeworkType}
group by suh.userid ) uh on uh.userid=a.id where a.user_type='3'
and exists ( select 1 from sys_user_office tsuo where tsuo.userid=#{teacher.id} and tsuo.officeid=suo.officeid )
and suo.officeid=#{office.id}
AND so.name like CONCAT('%', #{office.name}, '%')
ORDER BY ${page.orderBy}
ORDER BY cast( ifnull(a.score,'0') as SIGNED INTEGER) desc

SELECT ifnull(a.id,'') as "user.id", ifnull(a.name,'') as "user.name", ifnull(a.english_name,'') AS "user.englishName", ifnull(a.score,'') AS "user.score", ifnull(a.phone,'') AS "user.phone", ifnull(a.mobile,'') AS "user.mobile", ifnull(a.photo,'') AS "user.photo", ifnull(so.id,'') as "office.id", ifnull(so.name,'') as "office.name", ifnull(uh.total,'0') as "homeworkFinishToal" FROM sys_user a left join sys_user_office suo on a.id=suo.userid left join sys_office so on suo.officeid=so.id left join ( select count(*) total ,suh.userid from sys_user_homework suh left join homework h on suh.homeworkid=h.id where suh.state in ('1','2')
AND h.type =#{homeworkType}
group by suh.userid ) uh on uh.userid=a.id where a.user_type='3'
and exists ( select 1 from sys_user_office tsuo where tsuo.userid=#{teacher.id} and tsuo.officeid=suo.officeid )
and suo.officeid=#{office.id}
AND so.name like CONCAT('%', #{office.name}, '%')
ORDER BY ${page.orderBy}
ORDER BY cast( ifnull(uh.total,'0') as SIGNED INTEGER) desc

SELECT ifnull(a.id,'') as "user.id", ifnull(a.name,'') as "user.name", ifnull(a.english_name,'') AS "user.englishName", ifnull(a.score,'') AS "user.score", ifnull(a.phone,'') AS "user.phone", ifnull(a.mobile,'') AS "user.mobile", ifnull(a.photo,'') AS "user.photo", ifnull(so.id,'') as "office.id", ifnull(so.name,'') as "office.name", ifnull(suh.homeworkFinishToal,0) as "homeworkFinishToal", ifnull(suh.homeworkNotFinishToal,0) as "homeworkNotFinishToal" FROM sys_user a left join sys_user_office suo on a.id=suo.userid left join sys_office so on suo.officeid=so.id left join ( select userid ,sum(case when state='0' then 1 else 0 end) as homeworkNotFinishToal ,sum(case when state='1' then 1 else 0 end) as homeworkFinishToal from sys_user_homework
and finish_date between str_to_date(CONCAT(#{beginTime},' 00:00:00'),'%Y-%m-%d %H:%i:%s') and str_to_date(CONCAT(#{endTime},' 23:59:59'),'%Y-%m-%d %H:%i:%s')
and officeid=#{office.id}
group by userid ) suh on a.id=suh.userid where a.user_type='3'
and exists ( select 1 from sys_user_office tsuo where tsuo.userid=#{teacher.id} and tsuo.officeid=suo.officeid )
and suo.officeid=#{office.id}
AND so.name like CONCAT('%', #{office.name}, '%')
ORDER BY ${page.orderBy}
ORDER BY suh.homeworkFinishToal desc
